import logo from './logo.svg';
import './App.css';
import { useState, useEffect } from 'react';
function App() {
  const [no, setNo] = useState(0);
  const increment = () => {
    setNo(no + 1)
  }
  const reset = () => {
    setNo(0)
  }
  const decrement = () => {
    if (no > 0) {
      setNo(no - 1);
    }
  }
  useEffect(() => {
    console.log('Current value:- ', no);

  }, [no]);

  const containerStyle = {
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    justifyContent: 'center',
    minHeight: '100vh', // Ensure the container takes the full viewport height
    backgroundColor: '#e6f7ff', // Light blue background color
  };

  const counterStyle = {
    fontSize: '20px', // 20px font size for the counter
    margin: '20px',
  };
  return (
    <div style={containerStyle}>
    <h1 style={counterStyle}>{no}</h1>
    <button style={buttonStyle} onClick={increment}>Increment</button>
    <button style={buttonStyle} onClick={reset}>Reset</button>
    <button style={buttonStyle} onClick={decrement}>Decrement</button>
  </div>

  );
}

export default App;
